mapper.select:
select

from category_info
where slug = #{slug, jdbcType=BIGINT}

mapper.update:
update category_info
SET `title` = #{title, jdbcType=VARCHAR}, `slug` = #{slug, jdbcType=BIGINT}
WHERE `id` = #{id, jdbcType=INTEGER}

update category_info
SET `title` = #{title, jdbcType=VARCHAR}
WHERE `slug` = #{slug, jdbcType=BIGINT}
